    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
  try {
        Class.forName("com.mysql.cj.jdbc.Driver");
        Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/prueba", "AlbertoMN", "C4municipal*");

        // Get a PreparedStatement using a statement that retrieves the auto-generated ID
        String sql = "INSERT INTO empleados (nombre, apellido, salario, email) VALUES (?, ?, ?, ?)";
        PreparedStatement pstmt = con.prepareStatement(sql, Statement.RETURN_GENERATED_KEYS);

        pstmt.setString(1, nombre.getText());
        pstmt.setString(2, apellido.getText());
        pstmt.setInt(3, Integer.parseInt(salario.getText()));
        pstmt.setString(4, email.getText());

        int rowsAffected = pstmt.executeUpdate();

        if (rowsAffected == 1) {
            // Retrieve the auto-generated ID (if applicable)
            ResultSet generatedKeys = pstmt.getGeneratedKeys();
            if (generatedKeys.next()) {
                int generatedID = generatedKeys.getInt(1);
                JOptionPane.showMessageDialog(null, "Se a realizado el registro");
            } else {
                JOptionPane.showMessageDialog(null, "Se ha realizado el registro, pero no se pudo obtener el ID generado.");
            }
        } else {
            JOptionPane.showMessageDialog(null, "Error al insertar registro.");
        }

        con.close();

    } catch (Exception e) {
        JOptionPane.showMessageDialog(null, "Error: " + e.getMessage());
    }
    }//GEN-LAST:event_jButton2ActionPerformed

